import React from "react";
import { Card, Grid } from "semantic-ui-react";
import "./People.css";
import Images from "./characters.json";

function People({ data }) {
  // console.log(`url`, Images[0].url);

  return (
    <>
      <div className="title">People</div>
      <Grid
        stackable
        columns={4}
        style={{ padding: "20px 0 40px 0", minHeight: "2100px" }}
      >
        {data.map((person, index) => {
          const img = Images.filter((img) => img.name === person.name);
          console.log(`img`, img);
          // console.log("inedx", index);
          return (
            <Grid.Column key={index}>
              <Card>
                <Card.Content>
                  <Card.Content
                    textAlign="center"
                    style={{ height: "40px", fontSize: "16px" }}
                  >
                    {person.name}
                  </Card.Content>
                  <img
                    style={{ padding: "10px 0" }}
                    src="{img[0].url}"
                    alt="people"
                    width="100%"
                    height="auto"
                  />
                  <Card.Description>
                    <strong>Height</strong>
                    <p>{person.height}</p>
                    <strong>Mass</strong>
                    <p>{person.mass}</p>
                    <strong>Hair Color</strong>
                    <p>{person.hair_color}</p>
                  </Card.Description>
                </Card.Content>
              </Card>
            </Grid.Column>
          );
        })}
      </Grid>
    </>
  );
}

export default People;
