import React from 'react'
import {Card, Grid} from 'semantic-ui-react'
import Look from '../Look'
import "./Ships.css"
function Ships({data}) {
     const [searchTerm, setSearchTerm] = React.useState("");
 const [searchResults, setSearchResults] = React.useState([]);
 const handleChange = event => {
    setSearchTerm(event.target.value);
  };
 React.useEffect((data) => {
    const results ={data}[0].name.filter(person =>
      person.name.toLowerCase().includes(searchTerm)
    );
    setSearchResults(results);
  }, [searchTerm]);

    return (
        <> 
        <Look/>
              <input
        type="text"
        placeholder="Search"
        value={searchTerm}
        onChange={handleChange}
      />
      <ul>
         {searchResults.map(item => (
          <li>{item}</li>
        ))}
      </ul>
        <div classNmae className="title">Ships</div>

             <Grid columns={4}>
                 {data.map((ships, index) =>{
                     return (
                     <Grid.Column style={{margin:"20px 0"}} key={index}>
                         <Card >
                             <Card.Content>
                                <Card.Content style={{ fontSize:"16px"}}>{ships.name}</Card.Content>
                                <img style={{ padding:"10px 0"}} src = "https://i.pinimg.com/originals/96/bf/bd/96bfbda81e65ed260074ee0054ec1334.gif" alt="people" width="100%" height ="auto"/>
                                <Card.Description >
                                    <strong>Length</strong>
                                    <p>{ships.length}</p>
                                    <strong>Films</strong>
                                    <p>{ships.model}</p>
                                    <strong>Passengers</strong>
                                    <p>{ships.passengers}</p>
                                </Card.Description>
                             </Card.Content>
                         </Card>
                     </Grid.Column>)
                 })}
             </Grid>
        </>
    )
}

export default Ships
