import React,{useState} from 'react'
import {Card, Grid} from 'semantic-ui-react'
function Ships({data}) {
    const [input, setInput] = useState('')
    const handleChange = (e) =>{
        e.preventDefault();
        setInput(e.target.value);
    }
    if (input.length > 0) {
        data = data.filter((n)=>{
        return n.name.toLowerCase().match(input)
    })
    } else {

    }
    return (
        <> 

        <div style={{color: 'white', textAlign: 'center', fontSize:"36px", padding:"20px"}}>Ships</div>
        <input type="text" placeholder ="Search" value={input} onChange={handleChange}/>
             <Grid  stackable columns={4}>
                 {data.map((person, index) =>{
                     return (
                     <Grid.Column style={{margin:"20px 0"}} key={index}>
                         <Card >
                             <Card.Content>
                                <Card.Content style={{ fontSize:"16px"}}>{person.name}</Card.Content>
                                <img style={{ padding:"10px 0"}} src = "https://i.pinimg.com/originals/96/bf/bd/96bfbda81e65ed260074ee0054ec1334.gif" alt="people" width="100%" height ="auto"/>
                                <Card.Description >
                                    <strong>Length</strong>
                                    <p>{person.length}</p>
                                    <strong>Films</strong>
                                    <p>{person.model}</p>
                                    <strong>Passengers</strong>
                                    <p>{person.passengers}</p>
                                </Card.Description>
                             </Card.Content>
                         </Card>
                     </Grid.Column>)
                 })}
             </Grid>
        </>
    )
}

export default Ships
