import React, { useState, useEffect } from 'react';
import {Segment,} from 'semantic-ui-react'
import Images from "./img.json"
function Films() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
    // const [open, setOpen] = useState(false)
    // const  handleOpen = () => setOpen(true)
    // const handleClose = () => setOpen(false)
  useEffect(() => {
    fetch("https://swapi.dev/api/films/")
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result.results);
          console.log(result.results);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])
  console.log(`wewe`,Images.episode_id)
  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
  <div>
    <ul>
      {items.map(item => 
      <li key={item.episode_id}>
        <h2>{item.title}</h2>

            <div class="ui move reveal">
                <div class="visible content">
                    <img  src="https://mir-s3-cdn-cf.behance.net/project_modules/max_1200/aacb7487679287.5dc0077e9e7a7.jpg"  class="ui large image" alt="Wire"/>
                    {/* <img class="logo" src="assets/img/{{film.episode_id}}.png" alt=""></img> */}
                </div>
                <div class="hidden content">
                      <Segment style={{color:"black"}}>
                          <h3>{item.title}</h3>
                          <p style={{textAlign: 'justify'}}>{item.opening_crawl}</p>
                          <strong>Director: {item.director}</strong>
                          <strong>Producer: {item.producer}</strong>
                          <strong>Released: {item.release_date}</strong>
                      </Segment>
                </div>
            </div>

        </li>)}
    </ul>
</div>

    );
  }
}

export default Films
