import React from "react";
import { Card, Grid, Image } from "semantic-ui-react";
import "./People.css";
import Img from "./characters.json";
function People({ data }) {
  return (
    <>
      <div className="title">People</div>
      <Grid
        stackable
        columns={4}
        style={{ margin: "20px 0 40px 0", minHeight: "2100px" }}
      >
        {data.map((person, index) => {
          return (
            <Grid.Column key={index}>
              <Card>
                <Card.Content>
                  <Card.Content
                    textAlign="center"
                    style={{ height: "40px", fontSize: "16px" }}
                  >
                    {person.name}
                  </Card.Content>
                  <Image
                    src={`${Img[index].url}`}
                    class="ui large image"
                    alt="poster"
                  />
                  <Card.Description>
                    <strong>Height</strong>
                    <p>{person.height}</p>
                    <strong>Mass</strong>
                    <p>{person.mass}</p>
                    <strong>Hair Color</strong>
                    <p>{person.hair_color}</p>
                  </Card.Description>
                </Card.Content>
              </Card>
            </Grid.Column>
          );
        })}
      </Grid>
    </>
  );
}

export default People;
