import React, { useState, useEffect } from 'react';
import {
  Button,
  Segment,
  TransitionablePortal,
} from 'semantic-ui-react'

function Films() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
    const [open, setOpen] = useState(false)
    const  handleOpen = () => setOpen(true)
    const handleClose = () => setOpen(false)
  useEffect(() => {
    fetch("https://swapi.dev/api/films/")
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result.results);
          console.log(result.results);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])
  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
  <div>
    <ul>
      {items.map(item => 



      <li key={item.episode_id}>

            <div class="ui move reveal">
                <div class="visible content">
                    <img src="https://mir-s3-cdn-cf.behance.net/project_modules/fs/ffdc5e90601171.5e1c27d79a475.jpg" class="ui small image" alt="Wire"/>
                </div>
                <div class="hidden content">
                      <Segment style={{ left: '20%', position: 'fixed', top: '33%', zIndex: 1000, display: 'flex', flexDirection: 'column',width:'300px'}}>
                          <h3>{item.title}</h3>
                          <p style={{textAlign: 'justify'}}>{item.opening_crawl}</p>
                          <p>Director: {item.director}</p>
                          <p>Producer: {item.producer}</p>
                          <p>Released: {item.release_date}</p>
                      </Segment>
                </div>
            </div>
       <h3>{item.title}</h3> 
       <img src="https://mir-s3-cdn-cf.behance.net/project_modules/max_1200/d72fcc25747423.5634a1f258423.jpg" alt="poster" width="300" height="auto"/>
      {/* <p>{item.opening_crawl}</p> */}
      <TransitionablePortal
        closeOnTriggerClick
        onOpen={handleOpen}
        onClose={handleClose}
        openOnTriggerClick
        trigger={
          <Button
            content={open ? 'Thanks' : 'More Info'}
            negative={open}
            positive={!open}
            style={{width:"92%"}}
          />
        }
      >
        <Segment
          style={{ left: '20%', position: 'fixed', top: '33%', zIndex: 1000, display: 'flex', flexDirection: 'column',width:'300px'}}
        >
          <h3>{item.title}</h3>
            <p style={{textAlign: 'justify'}}>{item.opening_crawl}</p>
            <p>Director: {item.director}</p>
            <p>Producer: {item.producer}</p>
            <p>Released: {item.release_date}</p>
        </Segment>
      </TransitionablePortal>
        </li>)}
    </ul>
</div>

    );
  }
}

export default Films
